import { Container, Row, Col, Table } from "react-bootstrap"
import '../App.css';
import React, { useState } from 'react'
import axios from 'axios'
import { useInterval } from './utils';



export default function TableData() {
    const [datas, setData] = useState([])
    const [filter, setFilter] = useState('0')

    const url = `http://127.0.0.1:8000/api/?filter=${filter}`

    const getFilterData = () => {
        axios.get(url).then((response) => {
            setData(response.data)
            console.log(datas)
        }
        )
    }
    useInterval(() => {
        getFilterData()
    }, 1000 * 10);

    const filterLockToken = () => {
        setFilter('lockToken')
    }

    const filterStake = () => {
        setFilter('Stake')
    }

    const filterReset = () => {
        setFilter('0')
    }


    return (
        <>  <section className="skill">
            <Container>
                <Row>
                    <Col xs={12} md={3} xl={3} className="left">
                        <button onClick={filterReset}>
                            <div className="table-button">
                            <span>All</span>
                        </div>
                        </button>
                        <button className="margin" onClick={filterLockToken}>
                            <div className="table-button">
                            <span>Lock Token</span>
                        </div>
                        </button>
                        <button className="margin" onClick={filterStake}>
                            <div className="table-button">
                            <span>Stake</span>
                        </div>
                        </button>
                    </Col>

                </Row>
                <Row>
                    <Col xs={12} md={10} xl={10}>
                        <div className="table-bx">
                            <Table responsive>
                                <thead className="th">
                                    <tr>
                                        <th>Txn Hash</th>
                                        <th>Quantity</th>
                                        <th>Method</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {!datas || datas.length <= 0 ? (
                                        <tr>
                                            <td colSpan="6" align="center">
                                                <b>Data loading... Please a wait</b>
                                            </td>
                                        </tr>
                                    ) : (
                                        datas.map(transaction => (
                                            <tr key={transaction.id}>
                                                <td>{transaction.Hash}</td>
                                                <td>{transaction.Amount}</td>
                                                <td>{transaction.Method}</td>
                                            </tr>
                                        ))
                                    )}
                                </tbody>
                            </Table>
                        </div>
                    </Col>
                </Row>
            </Container>
        </section>
        </>
    );
}
